import asyncio
import websockets

async def test_connection():
    uri = "ws://localhost:8000/ws"
    try:
        async with websockets.connect(uri) as websocket:
            print("Connected to WebSocket successfully!")
            # 100x100 black jpeg base64
            # Generated via python: 
            # img = np.zeros((100,100,3), np.uint8)
            # _, buf = cv2.imencode('.jpg', img)
            # base64.b64encode(buf)
            fake_image = "data:image/jpeg;base64,"
            await websocket.send(f'{{"image": "{fake_image}", "gender": "female", "use_ai_model": true}}')
            print("Sent test message with image")
            
            response = await websocket.recv()
            print(f"Received response: {response[:50]}...")
            
            # Just wait a bit or close
            await websocket.close()
    except Exception as e:
        print(f"Failed to connect: {e}")

if __name__ == "__main__":
    asyncio.run(test_connection())
